import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import BannerLeague from '../BannerLeague/BannerLeague';
import LeagueDetailsCard from '../LeagueDetailsCard/LeagueDetailsCard';
import './LeagueDetails.css';

const LeagueDetails = () => {
    const {leagueId} = useParams();

    const [league, setLeague] = useState({});
    useEffect(() => {
        const url = `https://www.thesportsdb.com/api/v1/json/1/lookupleague.php?id=${leagueId}`;
        fetch(url)
        .then(res => res.json())
        .then(data => {
            setLeague(data.leagues)
            console.log(data.leagues);
        })
    }, [leagueId])

    return (
        <div>
            {/* <h1>Name: {league.strLeague}</h1> */}
            <BannerLeague></BannerLeague>
            <LeagueDetailsCard></LeagueDetailsCard>
        </div>
    );
};


export default LeagueDetails;
