import React from 'react';
import maleLeagueImg from '../../images/male.png';
import iconFound from '../../icons/found.png';
import iconFlag from '../../icons/flag.png';
import iconFootball from '../../icons/football.png';
import iconGender from '../../icons/male-gender-sign 1.png';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FaFacebook, FaTwitterSquare } from 'react-icons/fa';
import './LeagueDetailsCard.css';

const LeagueDetailsCard = () => {
    return (
        <div>
            <div className="container">
                <div className="d-flex bg-info mt-4 league">
                    <div className="col-md-6">
                        <div className="p-4 text-white leagueInfo">
                            <h2 className="font-weight-bold">Premier League</h2>
                            <p>
                                <img src={iconFound} alt=""/>
                                <span className="ml-3">Founded: February 20, 1982</span>
                                </p>
                            <p>
                                <img src={iconFlag} alt=""/>
                                <span className="ml-3">Country: England</span>
                            </p>
                            <p>
                                <img src={iconFootball} alt=""/>
                                <span className="ml-3">Sports Type: Football</span>
                            </p>
                            <p>
                                <img src={iconGender} alt=""/>
                                <span className="ml-3">Sports Type: Football</span>
                            </p>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="p-4">
                            <img src={maleLeagueImg} className="img-fluid" alt=""/>
                        </div>
                    </div>
                </div>

                <div className="description">
                    <p className="text-white mt-3 mb-5">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Commodi laudantium saepe nostrum asperiores modi minima. Nulla non minus saepe. Quidem aut unde itaque incidunt autem blanditiis labore pariatur reiciendis molestiae!</p>
                    <p className="text-white">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Commodi laudantium saepe nostrum asperiores modi minima. Nulla non minus saepe. Quidem aut unde itaque incidunt autem blanditiis labore pariatur reiciendis molestiae!</p>
                </div>

                <div className="social-icons">
                    <FaFacebook color="purple" size="3rem" className="bg-white fbIcon ml-3" />
                    <FaTwitterSquare  color="purple" size="3rem" className="bg-white fbIcon"></FaTwitterSquare>
                </div>
            </div>
        </div>
    );
};

export default LeagueDetailsCard;
