import React, { useEffect } from 'react';
import img from '../../images/image 1.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import "./LeagueList.css";

const LeagueList = (props) => {
    const {strLeague, strSport, id} = props.league;
    console.log(strLeague);

    //For logo
    // const [leagueLogo, setLeagueLogo] = useState([]);
    useEffect(() => {
        const api = `https://www.thesportsdb.com/api/v1/json/1/lookupleague.php?id=${id}`;
        fetch(api)
        .then(res => res.json())
        .then(data => {
            console.log(data.leagues);
        })
    })
    
    return (
        
        <div className="col-12 col-md-6 col-lg-4">
            <div className="card mb-5">
                <img src={img} className="card-img-top" alt=""/>
                <div className="card-body">
                    <h5 className="card-title">{strLeague}</h5>
                    <p className="card-text">Sports type: {strSport}</p>
                </div>
                <div className="footer-card">
                    <button className="btn btn-custom px-3 mt-3  fw-bold text-white ms-3">
                        Explore
                        <FontAwesomeIcon icon={faArrowRight} className="ml-2"></FontAwesomeIcon>
                    </button>
                </div>
            </div>
        </div>
    );
};

export default LeagueList;
