import React from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../../../node_modules/bootstrap/dist/js/bootstrap.bundle.min.js';
import bannerLeagueImg from '../../images/banner.jpg';

const BannerLeague = () => {
    const bannerStyle = {
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.7)), url(${bannerLeagueImg})`,
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center center',
        backgroundSize: 'cover',
        width: '100%',
        height: '180px'
    }

    return (
        <div>
            <div style={bannerStyle} className="d-flex justify-content-center align-items-center">
                
            </div>
        </div>
    );
};

export default BannerLeague;
