import React from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../../../node_modules/bootstrap/dist/js/bootstrap.bundle.min.js';
// import bannerLeagueImg from '../../images/banner.png';
import './BannerLeague.css';

const BannerLeague = (props) => {
    const league = props.league;
    // console.log(league);
    const bannerStyle = {
        backgroundImage: `linear-gradient(rgb(138, 135, 135, 0.6), rgb(138, 135, 135, 0.7)), url(${league.strBanner})`,
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center center',
        backgroundSize: 'cover',
        width: '100%',
        height: '250px'
    }

    return (
        <div>
            <div style={bannerStyle} className="d-flex justify-content-center align-items-center">
                <img src={league.strBadge} alt="" className="leagueLogo"/>
            </div>
        </div>
    );
};

export default BannerLeague;
