import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import LeagueList from '../LeagueList/LeagueList';
import "./League.css";
const League = () => {
    const [leagues, setLeagues] = useState([]);
    const leagues12 = leagues.slice(0, 12);

    //Load More Leagues
    const loadMoreData =  () => {
        setLeagues(previousLeague => previousLeague + 12)
    };

    useEffect(() => {
        fetch("https://www.thesportsdb.com/api/v1/json/1/all_leagues.php")
        .then(res => res.json())
        .then(data => {
            setLeagues(data.leagues)
        })
    }, [])

    return (
        <div>
            <div className="text-white container mt-4 leagueCard">
                <div className="card-group row">
                    {
                        leagues12.map(league => <LeagueList league={league} key={league.idLeague}></LeagueList>)
                    }
                </div>
                <div className="card-btn ">
                    <button onClick={loadMoreData} className="btn btn-custom mt-2 text-white ms-3 d-block m-auto">Load More</button>
                </div>
            </div>
        </div>
    );
};

export default League;
