import React from 'react';
import maleLeagueImg from '../../images/male.png';
import icon from '../../images//found.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight, faFlag, faFutbol, faMarsStroke } from '@fortawesome/free-solid-svg-icons';
import './LeagueDetails.css';

const LeagueDetails = () => {
    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6">
                    <div className="p-4 text-white leagueInfo">
                        <h2 className="font-weight-bold">Premier League</h2>
                        <p>
                            <img src={icon} alt=""/>
                            <span className="ml-3">Founded: February 20, 1982</span>
                            </p>
                        <p>
                            <FontAwesomeIcon icon={faFlag}></FontAwesomeIcon>
                            <span className="ml-3">Country: England</span>
                        </p>
                        <p>
                            <FontAwesomeIcon icon={faFutbol}></FontAwesomeIcon>
                            <span className="ml-3">Sports Type: Football</span>
                        </p>
                        <p>
                            <FontAwesomeIcon icon={faMarsStroke}></FontAwesomeIcon>
                            <span className="ml-3">Sports Type: Football</span>
                        </p>
                    </div>
                </div>
                <div className="col-md-6">
                    <div className="p-4">
                        <img src={maleLeagueImg} className="img-fluid" alt=""/>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LeagueDetails;
